package myprograms;

public class Length_vs_length {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int array []= {4,6,2,1,6};
		//length is a instance variable which is used to get lenght of the array
		
		int size = array.length;
		System.out.println(size);
		
		//length() - is used to get length of the string
		String a = "Happy Boy";
		int stringsize = a.length();
		System.out.println(stringsize);
	}

}
